package section1;

//example
public class Q4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//widening(automatic)
		int num1=10;
		System.out.println(num1);
		double num2=num1;
		System.out.println(num2);
		
		//narrowing
		float num3=10.5f;
		System.out.println(num3);
		int num4=(int)num3;
		System.out.println(num4);
		
	}	

}
