package section2;

public class Q5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				//while
				int i=1;
				while(i<=10) {
					System.out.println(i);
					i++;
				}
				
				//do while
				int j=1;
				do{
					System.out.println("Hello");
					j++;
				}while(j<5);
	}

}
